import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { Categories } from 'src/app/_models/categories.model';
import { CategoriesService } from 'src/app/_services/categories.service';

@Component({
  selector: 'app-categories',
  templateUrl: './categories.component.html',
  styleUrls: ['./categories.component.css']
})
export class CategoriesComponent implements OnInit {

  categoriesdata!: Categories[];

  constructor(private http: HttpClient, private categoriesService: CategoriesService, private router: Router) { }

  ngOnInit(): void {
    this.getcategorydata();
  }

  getcategorydata() {
    this.categoriesService.get().subscribe(res => { this.categoriesdata = res; });
  }

}
