import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { User } from 'src/app/_models/user';
import { UsersService } from 'src/app/_services/users.service';

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.css']
})
export class UsersComponent implements OnInit {

  userdata!:Observable<User[]>;
  user!:User;

  constructor(private userservice:UsersService) { }

  ngOnInit(): void {
    this.getallUsers();
  }

  getallUsers(){
    this.userdata = this.userservice.getall();
  }

  async setuserid(id:number){
    await this.userservice.getbyid(id).subscribe(res=>{
      this.user = res;
      this.callmodel(res);
    });
  }

  callmodel(data:User)
  {
    this.updateSubCategoryForm.controls["name"].setValue(data.name);
    this.updateSubCategoryForm.controls["categoryid"].setValue(data.categoryid);
    document.getElementById('openModalButton')?.click();
  }

  deleteuser(id:number){

  }
}
